import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios'
import React, {useState, useEffect} from 'react';

const api = axios.create({
  baseURL: 'http://localhost:5001',
});

function CoinBlock({ name, price, volume, change}) {
  return (
  <div className="card">
    <div className="card-body">
      <h5 className="card-title">{name}</h5>
      <h6 class="card-subtitle mb-2 text-muted">{price}$</h6>
      <div className="row">
        <div className="col-6">
          <h8>volume:</h8>
          <p>{volume}$</p>
        </div>
        <div className="col-6">
          <h8>change:</h8>
          <p>{change}%</p>
        </div>
      </div>
    </div>  
  </div>
  );
}

function App() {
  const [prices, setPrices] = useState([]);

  useEffect(() => {
    const fetchPrices = () => {
      api.get('/api/prices').then(
      response => { 
        setPrices(response.data.data);
        console.log(response.data.data[0]);
      }).catch(error => console.log("Error: ", error));
    }

    fetchPrices();

    const intervalId = setInterval(() => {
      fetchPrices();
    }, 3000);

    return () => clearInterval(intervalId);
  }, []);

  if (prices.length == 0) {
    return <div>Loading...</div>
  }
  const dataArray = prices.slice(0, 11).map(coin => ({
    name: coin.name,
    price: parseFloat(coin.priceUsd).toFixed(8),
    volume: parseFloat(coin.volumeUsd24Hr).toFixed(5),
    change: parseFloat(coin.changePercent24Hr).toFixed(8)
  }));

  return (
    <div className="d-flex flex-column justify-content-center mt-2">
      <div className="d-flex justify-content-center">
        <h2>Cryptocurrency Realtime Prices</h2>
      </div>

      <div className="container mt-3">
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {dataArray.map( (data, index) => (
            <div className="col" key={index}> {/* Use just "col" here */}
              <CoinBlock 
                name = {data.name}
                price = {data.price}
                volume = {data.volume}
                change = {data.change}
              />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
