import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios'
import React, {useState, useEffect} from 'react';

const api = axios.create({
  baseURL: 'http://localhost:5001',
});

function CoinBlock() {
  return (
    <div className="card text-center">
    <div className="card-header">
      {name}
    </div>
    <div className="card-body">
      <h5 className="card-title">{priceFormatter.format(price)}</h5>
      <p className="card-text">volume: {numberFormatter.format(volume)}</p>
      <p className="card-text">change: {numberFormatter.format(change)}</p>
    </div>
  </div>
  );
}

function App() {
  const [prices, setPrices] = useState([]);

  useEffect(() => {
    api.get('/api/prices').then(
    response => { 
      setPrices(response.data.data);
      console.log(response.data.data[0]);
    }).catch(error => console.log("Error: ", error));
  }, []);

  return (
    <div className="d-flex flex-column justify-content-center mt-2">
      <div className="d-flex justify-content-center">
        <h2>Cryptocurrency Realtime Prices</h2>
      </div>
      <div className="d-flex justify-content-center">
        <CoinBlock />
      </div>
    </div>
  );
}

export default App;
