import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios'
import React, {useState, useEffect} from 'react';

const api = axios.create({
  baseURL: 'http://localhost:5001',
});

function CoinBlock() {
  return (
  <div className="card">
    <div className="card-body">
      <h5 className="card-title">Bitcoin</h5>
      <h6 class="card-subtitle mb-2 text-muted">Card subtitle</h6>
      <a href="#" class="card-link">Card link</a>
      <a href="#" class="card-link">Another link</a>
    </div>  
  </div>
  );
}

function App() {
  const [prices, setPrices] = useState([]);

  useEffect(() => {
    api.get('/api/prices').then(
    response => { 
      setPrices(response.data.data);
      console.log(response.data.data[0]);
    }).catch(error => console.log("Error: ", error));
  }, []);

  const mockDataArray = [ { name: 'Bitcoin', price: 4308.29936156, volume: 27991.55689133, change: -11.73364494 }, { name: 'Bitcoin', price: 4308.29936156, volume: 27991.55689133, change: -11.73364494 }, { name: 'Bitcoin', price: 4308.29936156, volume: 27991.55689133, change: -11.73364494 }, { name: 'Bitcoin', price: 4308.29936156, volume: 27991.55689133, change: -11.73364494 }, ];

  return (
    <div className="d-flex flex-column justify-content-center mt-2">
      <div className="d-flex justify-content-center">
        <h2>Cryptocurrency Realtime Prices</h2>
      </div>
      <div className="d-flex justify-content-center">
        {mockDataArray.map((data, index) => (
            <div className="col-md-4 mb-4" key={index}>
              <CoinBlock 
                name={data.name} 
                price={data.price} 
                volume={data.volume} 
                change={data.change} 
              />
            </div>
          ))}
      </div>
    </div>
  );
}

export default App;
